const burger=document.querySelector(".main__burger"),navigation=document.querySelector(".main-navigation"),burgerBtn=document.querySelector(".burger-name"),logo=document.querySelector(".sm-logo");burger.addEventListener("click",(t=>{t.target.classList.contains("active")?(t.currentTarget.classList.remove("active"),navigation.classList.remove("active"),burgerBtn.classList.remove("active"),logo.classList.remove("active")):(t.currentTarget.classList.add("active"),navigation.classList.add("active"),burgerBtn.classList.add("active"),logo.classList.add("active"))}));const tabActivators=document.querySelectorAll(".tab_activator"),tabChilds=document.querySelectorAll(".tab_child");tabActivators.forEach((t=>{t.addEventListener("click",(function(t){tabActivators.forEach((t=>{t.classList.remove("active")})),t.target.classList.add("active"),tabChilds.forEach((a=>{t.target.dataset.tab==a.dataset.tab?a.classList.add("active"):a.classList.remove("active")}))}))}));